('30258', '297', 'Karen L. Simpson', 'ksimpson@umich.edu', 'http://lafreya.blogspot.com/', '141.211.223.175', '2009-10-02 14:52:00', '2009-10-02 18:52:00', 'Thank you for your essays and your blog post about race in publishing. As one of those writers of color participating in the Speculative Fiction community conversation on race I know how hard it is to have even a halfway rational conversation about this issue. As Zora Hurston would say all we can do is speak so we can speak again until  people are more willing to listen and understand.', '0', '1', 'Mozilla/4.0 (compatible; MSIE 7.0; Windows NT 5.1; .NET CLR 2.0.50727; .NET CLR 3.0.04506.648; .NET CLR 3.5.21022; InfoPath.2; .NET CLR 3.0.4506.2152; .NET CLR 3.5.30729)', '', '0', '0', 'N'),
('30182', '271', 'Tim Jones-Yelvington', 'timjy@sbcglobal.net', 'http://perverseadult.blogspot.com', '173.15.104.171', '2009-09-18 12:08:44', '2009-09-18 16:08:44', 'I love the Rumpus piece.', '0', '1', 'Mozilla/5.0 (Windows; U; Windows NT 5.1; en-US; rv:1.9.1.3) Gecko/20090824 Firefox/3.5.3', '', '0', '0', 'N'),
